import ( "slices" "sort" "strconv"
) func (h *HashRing) Add(nodes ...string) { for _, k := range nodes { exist := false for _, val := range h.keys { if val == k { exist = true } } if !exist { h.keys = append(h.keys, k) for i := 0; i < h.replicas; i++ { hash := int(h.hash([]byte(strconv.Itoa(i) + k))) h.ring = append(h.ring, hash) h.nodes[hash] = k } } } slices.Sort(h.ring)
}
func (h *HashRing) Len() int { return len(h.keys)
} func (h *HashRing) Get(key string) string { if h.Len() == 0 { return "" } hash := int(h.hash([]byte(key))) // Binary search for appropriate replica. idx := sort.Search(len(h.ring), func(i int) bool { return h.ring[i] >= hash }) // Means we have cycled back to the first replica. if idx == len(h.ring) { idx = 0 } return h.nodes[h.ring[idx]]
}
func (h *HashRing)Rest(){ h.ring = h.ring[:0] h.keys = h.keys[:0] clear(h.nodes)
} Add() 方法是用来创建节点环的,在一些节点退出后，可能需要reset 哈希环来再次重构哈希环 Reset() 是用来重置hash环信息的 Get() 就是用来获取 一致性哈希映射到的 节点的字符串上
